mapper.select:
select
	A.PART_NO 			no,
	A.PART_NAME			name,
	A.PART_PRICE		price,
	A.PART_COST			cost,
	A.PART_QTY			qty,
	B.PART_STATUS		"partStatus.status",
	C.DIVISION_NAME		"division.name",
	A.PART_INFO			info,
	A.PART_IMAGEPATH	imagepath
from
	sc_part A, PART_STATUS B, (select 
									A.DIVISION_ID,
									A.DIVISION_NAME
								from
									SC_DIVISION A, SC_CATEGORY B
								where
									A.CATEGORY_ID = B.CATEGORY_ID


										and
											A.CATEGORY_ID = #{category.id}


										and
											A.CATEGORY_ID = #{category.id}


										and
											A.CATEGORY_ID = #{category.id}


										and
											A.CATEGORY_ID = #{category.id}


										and
											A.CATEGORY_ID = #{category.id}


								) C
where
	A.PART_ID = B.PART_ID
and
	A.DIVISION_ID = C.DIVISION_ID

and	
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}


and
	A.DIVISION_ID = #{id}

order by
	A.PART_NO asc

select PART_NO no, PART_NAME name, PART_PRICE price, PART_COST cost, PART_QTY qty, PART_STATUS "partStatus.status", DIVISION_NAME "division.name", PART_INFO info, PART_IMAGEPATH imagepath from (select row_number() over (order by PART_NO desc) rn, A.PART_NO, A.PART_NAME, A.PART_MODEL, A.PART_PRICE, A.PART_COST, A.PART_QTY, B.PART_STATUS, C.DIVISION_NAME, A.PART_INFO, A.PART_IMAGEPATH from sc_part A, PART_STATUS B, (select A.DIVISION_ID, A.DIVISION_NAME from SC_DIVISION A, SC_CATEGORY B where A.CATEGORY_ID = B.CATEGORY_ID
and A.CATEGORY_ID = #{categoryId}
and A.CATEGORY_ID = #{categoryId}
and A.CATEGORY_ID = #{categoryId}
and A.CATEGORY_ID = #{categoryId}
and A.CATEGORY_ID = #{categoryId}
) C where A.PART_ID = B.PART_ID and A.DIVISION_ID = C.DIVISION_ID
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
and A.DIVISION_ID = #{divisionId}
) where rn >= #{beginIndex} and rn <= #{endIndex} order by PART_NO desc

