mapper.update:
update sc_part
PART_NAME = #{name}, PART_MODEL = #{modelName}, PART_PRICE = #{price}, PART_COST = #{cost}, PART_QTY = #{qty}, PART_ID = #{partStatus.id}, DIVISION_ID = #{division.id}, PART_INFO = #{info},
where part_no = #{no}
