mapper.select:
select * from(
select row_number() over (order by service_reservation desc) as rn , A.service_no as "no" , A.service_symptom as "symptom" , A.service_reservation as "reservation" , A.user_no as "user.no" , A.employee_no as "employee.no" , A.center_id as "center.id" , A.division_id as "division.id" , A.service_id as "serviceStatus.id" , S.service_status as "serviceStatus.status" , A.type_id as "serviceType.id" , A.service_address as "address" from sc_afterservice A, service_status S where A.employee_no = #{engineerNo} AND A.service_id = S.service_id
AND A.service_reservation >= #{reservation}
) where rn >= #{beginIndex} and rn <= #{endIndex}

mapper.update:
update sc_afterservice
set
	service_id = #{serviceStatus.id}
where
	service_no = #{no}
