mapper.select:
select notice_no as no, notice_title as title, notice_contents as contents, notice_createdate as createdate, notice_count as count from ( select row_number() over (order by notice_no desc) rn, notice_no, notice_title, notice_contents, notice_createdate, notice_count from customer_notice
and notice_title like '%' || #{keyword} || '%'
and notice_contents like '%' || #{keyword} || '%'
) where rn >= #{beginIndex} and rn <= #{endIndex}

mapper.update:
update customer_notice
notice_title = #{title}, notice_contents = #{contents}, notice_count = #{count},
where notice_no = #{no}
